mapper.insert:
insert into u_auth ( u_id, u_auth ) values
( #{username}, #{authority.role} )
ON DUPLICATE KEY UPDATE u_id = #{username}

mapper.delete:
		DELETE 
 		FROM 		u_auth 
 		WHERE 		u_auth = 'ROLE_ADMIN' && u_id = #{username}

mapper.update:
		UPDATE 				user
  	 	SET 				auth = #{auth}	
		WHERE 			u_id = #{username}

		UPDATE 				user
  	 	SET 				point = point + #{point}
		WHERE 				u_id = #{id}

mapper.select:
SELECT point
FROM user
WHERE u_id = #{id}

mapper.update:
		UPDATE 				user
  	 	SET 				isAccountNonLocked = false
		WHERE 				u_id = #{username}

		UPDATE 				user
  	 	SET 				isAccountNonLocked = true
		WHERE 				u_id = #{username}

		UPDATE 				user
  	 	SET 				name = #{name},
  	 						postcode = #{postcode},
  	 						address = #{address},
  	 						phone = #{phone} 
		WHERE 				u_id = #{username}

mapper.select:
SELECT wishitem AS code, id, option, count, num
FROM wishlist
WHERE id = #{username}

mapper.update:
		UPDATE 				user
  	 	SET 				point = point-#{point}
		WHERE 				u_id = #{id}
